// Função 1: Remove caption de links que não são imagens ou vídeos
function removeCaptionFromLinks(text) {
  // Expressão regular para identificar links que não são imagens ou vídeos
  const regexLinks = /\[([^\]]*)\]\((https?:\/\/[^\s]+)\)/gi;

  return text.replace(regexLinks, (match, caption, url) => {
    // Se o link não for de imagem ou vídeo, remove o caption e mantém só a URL
    if (!url.match(/\.(jpeg|jpg|png|gif|bmp|webp|avif|mp4|mov|avi|wmv)$/i)) {
      return url;
    }
    return match; // Mantém o link inalterado se for uma imagem ou vídeo
  });
}

const message = $input.item.json.output;

// Primeira função: remove captions de links não-imagens/vídeos
cleanedMessage = removeCaptionFromLinks(message);

return { output: cleanedMessage };

const message = $input.item.json.output;

const regex = /(?:\!\[([^\]]*)\]\((https?:\/\/[^\s]+?\.(?:jpeg|jpg|png|gif|bmp|webp|avif))\))|(?:\[([^\]]*)\]\((https?:\/\/[^\s]+?\.(?:jpeg|jpg|png|gif|bmp|webp|avif))\))|(?:\((https?:\/\/[^\s]+?\.(?:jpeg|jpg|png|gif|bmp|webp|avif))\))|((https?:\/\/[^\s]+?\.(?:jpeg|jpg|png|gif|bmp|webp|avif)))/gi;

const parts = [];
let lastIndex = 0;
let match;

while ((match = regex.exec(message)) !== null) {
  // Adiciona o texto entre os matches
  if (match.index > lastIndex) {
    let content = message.slice(lastIndex, match.index).replace(/^(\\n)+|(\\n)+$/g, '').trim();
    // Remove o '-' desnecessário no final do texto, se presente
    if (content.endsWith('-')) {
      content = content.slice(0, -1).trim();
    }
    if (content) {
      content = content.replace(/^(\\n)+|(\\n)+$/g, '').trim();
      parts.push({ type: 'text', content });
    }
  }

  // Adiciona as imagens ou links encontrados
  if (match[1]) {
    parts.push({ type: 'image', caption: match[1], url: match[2] });
  } else if (match[3]) {
    parts.push({ type: 'image', caption: match[3], url: match[4] });
  } else if (match[5] || match[6]) {
    parts.push({ type: 'image', url: match[5] || match[6] });
  }

  // Atualiza o último índice processado
  lastIndex = regex.lastIndex;
}

// Adiciona o texto final, se houver
if (lastIndex < message.length) {
  let finalContent = message.slice(lastIndex).replace(/^(\\n)+|(\\n)+$/g, '').trim();
  if (finalContent.endsWith('-')) {
    finalContent = finalContent.slice(0, -1).trim();
  }
  if (finalContent) {
    parts.push({ type: 'text', content: finalContent });
  }
}

// Verifica se a parte é um vídeo e adiciona
parts.forEach(part => {
  if (part.type === 'url' && part.url.match(/\.(mp4|mov|avi|wmv)$/)) {
    part.type = 'video';
  }
});

return { output: parts };
